@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: 0 0% 100%;
  --foreground: 0 0% 10%;
  --card: 0 0% 100%;
  --card-foreground: 0 0% 10%;
  --popover: 0 0% 100%;
  --popover-foreground: 0 0% 10%;
  --primary: 16 100% 50%;
  --primary-foreground: 0 0% 100%;
  --secondary: 0 0% 96%;
  --secondary-foreground: 0 0% 10%;
  --muted: 0 0% 96%;
  --muted-foreground: 0 0% 45%;
  --accent: 0 0% 96%;
  --accent-foreground: 0 0% 10%;
  --destructive: 0 84% 60%;
  --destructive-foreground: 0 0% 98%;
  --border: 0 0% 89%;
  --input: 0 0% 89%;
  --ring: 16 100% 50%;
  --radius: 8px;

  /* Custom ShootXpress colors */
  --shootx-black: hsl(0 0% 0%);
  --shootx-white: hsl(0 0% 100%);
  --shootx-orange: hsl(16 100% 50%);
  --shootx-gray: hsl(0 0% 45%);
  --shootx-light-gray: hsl(0 0% 96%);

  --font-sans: 'Inter', sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
}

.dark {
  --background: 0 0% 0%;
  --foreground: 200 6.6667% 91.1765%;
  --card: 228 9.8039% 10%;
  --card-foreground: 0 0% 85.0980%;
  --popover: 0 0% 0%;
  --popover-foreground: 200 6.6667% 91.1765%;
  --primary: 16 100% 50%;
  --primary-foreground: 0 0% 100%;
  --secondary: 195.0000 15.3846% 94.9020%;
  --secondary-foreground: 210 25% 7.8431%;
  --muted: 0 0% 9.4118%;
  --muted-foreground: 210 3.3898% 46.2745%;
  --accent: 205.7143 70% 7.8431%;
  --accent-foreground: 203.7736 87.6033% 52.5490%;
  --destructive: 356.3033 90.5579% 54.3137%;
  --destructive-foreground: 0 0% 100%;
  --border: 210 5.2632% 14.9020%;
  --input: 207.6923 27.6596% 18.4314%;
  --ring: 202.8169 89.1213% 53.1373%;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', sans-serif;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer components {
  .hero-bg {
    background: linear-gradient(135deg, var(--shootx-black) 0%, hsl(0 0% 10%) 100%);
  }

  .package-card {
    transition: all 0.3s ease;
  }

  .package-card:hover {
    transform: translateY(-8px);
    box-shadow: 0 20px 40px rgba(255, 69, 0, 0.1);
  }

  .nav-sticky {
    backdrop-filter: blur(10px);
    background: rgba(255, 255, 255, 0.95);
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out;
  }

  @keyframes fadeIn {
    from { 
      opacity: 0; 
      transform: translateY(20px); 
    }
    to { 
      opacity: 1; 
      transform: translateY(0); 
    }
  }

  .gallery-item {
    transition: transform 0.3s ease;
  }

  .gallery-item:hover {
    transform: scale(1.05);
  }


  .text-gradient {
    background: linear-gradient(135deg, var(--shootx-black) 0%, var(--shootx-orange) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
}

@layer components {
  .btn-primary {
    @apply bg-primary hover:bg-primary/90 text-primary-foreground font-bold transition-all duration-300;
  }

  .btn-secondary {
    @apply bg-secondary hover:bg-secondary/90 text-secondary-foreground font-bold transition-all duration-300;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

